import React, { Component } from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import boy from "./media/boy.png";
import girl from "./media/girl.png";
import man from "./media/man.png";
import lady from "./media/lady.png";
import Hooks from "../hoc/Hooks";

class Game extends Component {
  constructor(props) {
    super(props);
    this.state = {
      value1: null,
      value2: null,
      value3: null,
      value4: null,
      value5: null,
      value6: null,
      value7: null,
      value8: null,
      value9: null,
    };
  }
  render() {
    const Palyer = (e) => {
      const id = e.target.id;
      this.setState({ value1: Palyer });
    };
    return (
      <div className="row">
        <div className="col offset-m1 offset-s1 back-i-t">
          <Link to={"/menu"}>
            <i class="large back-icon">arrow_back</i>
          </Link>
        </div>
        <div class="col offset-m9 offset-s7 chip-box-t">
          <Link to={"/profile"} state={{ path: this.props.location.pathname }}>
            <div className="chip">
              <img
                src={require("./media/" + this.props.user.avatar + ".png")}
              ></img>
              {this.props.user.name}
            </div>
          </Link>
        </div>
        <div className="container-t">
          <div className="t-page">
            <div onClick={Palyer} id={1} className="col m4 s4 t-box"></div>
            <div onClick={Palyer} id={2} className="col m4 s4 t-box"></div>
            <div
              onClick={Palyer}
              id={3}
              className="col m4 s4 t-box border-r"
            ></div>
            <div onClick={Palyer} id={4} className="col m4 s4 t-box"></div>
            <div onClick={Palyer} id={5} className="col m4 s4 t-box"></div>
            <div
              onClick={Palyer}
              id={6}
              className="col m4 s4 t-box border-r"
            ></div>
            <div
              onClick={Palyer}
              id={7}
              lassName="col m4 s4 t-box border-b"
            ></div>
            <div
              onClick={Palyer}
              id={8}
              className="col m4 s4 t-box border-b"
            ></div>
            <div
              onClick={Palyer}
              id={9}
              className="col m4 s4 t-box border-r border-b"
            ></div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return { user: state.users };
};

export default connect(mapStateToProps)(Hooks(Game));
