import React, { Component } from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import boy from "./media/boy.png";
import girl from "./media/girl.png";
import man from "./media/man.png";
import lady from "./media/lady.png";
import Hooks from "../hoc/Hooks";

class Game extends Component {
  constructor(props) {
    super(props);
    this.state = {
      t1: { id: 1, value: null },
      t2: { id: 2, value: null },
      t3: { id: 3, value: null },
      t4: { id: 4, value: null },
      t5: { id: 5, value: null },
      t6: { id: 6, value: null },
      t7: { id: 7, value: null },
      t8: { id: 8, value: null },
      t9: { id: 9, value: null },
    };
  }
  render() {
    const Player = (e) => {
      let newState = this.state.find((item) => {
        item.id === e.target.id;
      });
      console.log(newState);
    };
    return (
      <div className="row">
        <div className="col offset-m1 offset-s1 back-i-t">
          <Link to={"/menu"}>
            <i class="large back-icon">arrow_back</i>
          </Link>
        </div>
        <div class="col offset-m9 offset-s7 chip-box-t">
          <Link to={"/profile"} state={{ path: this.props.location.pathname }}>
            <div className="chip">
              <img
                src={require("./media/" + this.props.user.avatar + ".png")}
              ></img>
              {this.props.user.name}
            </div>
          </Link>
        </div>
        <div className="container-t">
          <div className="t-page">
            <div onClick={Player} id={1} className="col m4 s4 t-box"></div>
            <div onClick={Player} id={2} className="col m4 s4 t-box"></div>
            <div
              onClick={Player}
              id={3}
              className="col m4 s4 t-box border-r"
            ></div>
            <div onClick={Player} id={4} className="col m4 s4 t-box"></div>
            <div onClick={Player} id={5} className="col m4 s4 t-box"></div>
            <div
              onClick={Player}
              id={6}
              className="col m4 s4 t-box border-r"
            ></div>
            <div
              onClick={Player}
              id={7}
              className="col m4 s4 t-box border-b"
            ></div>
            <div
              onClick={Player}
              id={8}
              className="col m4 s4 t-box border-b"
            ></div>
            <div
              onClick={Player}
              id={9}
              className="col m4 s4 t-box border-r border-b"
            ></div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return { user: state.users };
};

export default connect(mapStateToProps)(Hooks(Game));
