import React, { Component } from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import boy from "./media/boy.png";
import girl from "./media/girl.png";
import man from "./media/man.png";
import lady from "./media/lady.png";
import Hooks from "../hoc/Hooks";

class Game extends Component {
  constructor(props) {
    super(props);
    this.state = {
      Pl_id: null,
      parts: [
        { id: 1, value: null },
        { id: 2, value: null },
        { id: 3, value: null },
        { id: 4, value: null },
        { id: 5, value: null },
        { id: 6, value: null },
        { id: 7, value: null },
        { id: 8, value: null },
        { id: 9, value: null },
      ],
      Refs: [
        (this.ele1 = React.createRef()),
        (this.ele2 = React.createRef()),
        (this.ele3 = React.createRef()),
        (this.ele4 = React.createRef()),
        (this.ele5 = React.createRef()),
        (this.ele6 = React.createRef()),
        (this.ele7 = React.createRef()),
        (this.ele8 = React.createRef()),
        (this.ele9 = React.createRef()),
      ],
    };
  }
  render() {
    const Player = (e) => {
      e.target.className += " Pl";
      e.target.setAttribute("disabled", "disabled");
      this.setState({ Pl_id: e.target.id });
      let PL_ch = this.state.parts.filter((item) => {
        return item.id == this.state.Pl_id;
      });
      PL_ch[0].value = "Pl";
    };
    const Computer = () => {
      let Pc_ch = this.state.parts.filter((item) => {
        return item.value !== "Pl" && item.value !== "Pc";
      });
      Pc_ch = Pc_ch[Math.floor(Math.random() * Pc_ch.length)];
      Pc_ch.value = "Pc";

      this.state.Refs[Pc_ch.id - 1].current.classList.add("Pc");
      this.state.Refs[Pc_ch.id - 1].current.setAttribute(
        "disabled",
        "disabled",
      );
      const handleClick = () => {
        Player();
        Computer();
      };
    };

    return (
      <div className="row">
        <div className="col offset-m1 offset-s1 back-i-t">
          <Link to={"/menu"}>
            <i class="large back-icon">arrow_back</i>
          </Link>
        </div>
        <div class="col offset-m9 offset-s7 chip-box-t">
          <Link to={"/profile"} state={{ path: this.props.location.pathname }}>
            <div className="chip">
              <img
                src={require("./media/" + this.props.user.avatar + ".png")}
              ></img>
              {this.props.user.name}
            </div>
          </Link>
        </div>
        <div className="container-t">
          <div className="t-page">
            <div className="col m4 s4 t-box">
              <button
                onClick={this.handleClick}
                id={1}
                ref={this.ele1}
                className="btn-t"
              ></button>
            </div>
            <div className="col m4 s4 t-box">
              <button
                onClick={Player}
                id={2}
                ref={this.ele2}
                className="btn-t"
              ></button>
            </div>
            <div className="col m4 s4 t-box border-r">
              <button
                onClick={Player}
                id={3}
                ref={this.ele3}
                className="btn-t"
              ></button>
            </div>
            <div className="col m4 s4 t-box">
              <button
                onClick={Player}
                id={4}
                ref={this.ele4}
                className="btn-t"
              ></button>
            </div>
            <div className="col m4 s4 t-box">
              <button
                onClick={Player}
                id={5}
                ref={this.ele5}
                className="btn-t"
              ></button>
            </div>
            <div className="col m4 s4 t-box border-r">
              <button
                onClick={Player}
                id={6}
                ref={this.ele6}
                className="btn-t"
              ></button>
            </div>
            <div className="col m4 s4 t-box border-b">
              <button
                onClick={Player}
                id={7}
                ref={this.ele7}
                className="btn-t"
              ></button>
            </div>
            <div className="col m4 s4 t-box border-b">
              <button
                onClick={Player}
                id={8}
                ref={this.ele8}
                className="btn-t"
              ></button>
            </div>
            <div className="col m4 s4 t-box border-r border-b">
              <button
                onClick={Player}
                id={9}
                ref={this.ele9}
                className="btn-t"
              ></button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return { user: state.users };
}
export default connect(mapStateToProps)(Hooks(Game));
